bool CheckString(const char *str)
{
	FILE *_log = fopen("FVPLOG.txt", "a");
	bool ret = true;
	if(strlen(str) >= 0xFF)
	{
		fprintf(_log, "String [%s] is too long\n", str);
		fclose(_log);
		return false;
	}
	
	for(int i = 0; i < strlen(str); i++)
	{
		if(str[i] == '[')
		{
			bool token1 = false;
			bool token2 = false;
			for(int j = i + 1; j < strlen(str); j++)
			{
				if(str[j] == '|')
				{
					token1 = true;
					for(int k = j + 1; k < strlen(str); k++)
					{
						if(str[k] == ']')
						{
							token2 = true;
							goto end;
						}
					}
				}
			}

			end:
			if(!(token1 && token2))
			{
				ret = false;
				if(!token1)
				{
					fprintf(_log, "String [%s] missing token'|'\n", str);
				}
				if(!token2)
				{
					fprintf(_log, "String [%s] missing token']'\n", str);
				}
			}
			
		}
		
		if(str[i] == '~')
		{
			fprintf(_log, "Warning: String [%s] contains token'~' for drawing new line\n", str);
		}
			
		if(str[i] == '~')
		{
			fprintf(_log, "Warning: String [%s] contains token'~' for drawing new line\n", str);
		}
		
		if(str[i] == '{' || str[i] == '}')
		{
			fprintf(_log, "String [%s] contains token'{' for commit\n", str);
		}
		
		if(str[i] == '?')
		{
			fprintf(_log, "String [%s] contains char'?', maybe it is a unsuccessful translation mark\n", str);
		}
	}
	
	fclose(_log);
	return ret;
}

class TextPair
{
	public:
	TextPair();
	~TextPair();
	void Add(DWORD zPos, const char *rhs, int len);
	bool Find(DWORD zPos); 
	int  GetInfo(DWORD zPos, int &len, char *dst);
	void PrintAll();
	void GetAllOffset();
	void Close();
	map<DWORD, Textptr> TextLayer;
	map<DWORD, Textptr>::iterator it;
	typedef pair<DWORD ,Textptr> TextLayerPair; 
	
	private:
	FILE* filehandle;
};

void TextPair::GetAllOffset()
{
	for(it = TextLayer.begin(); it != TextLayer.end(); it++)
	{
		fprintf(filehandle,"STRING @ [%08x]",it->first);
	}
}

void TextPair::PrintAll()
{
	for(it = TextLayer.begin(); it != TextLayer.end(); it++)
	{
		fprintf(filehandle,"[%08x,%02x]%s\n",it->first,it->second.len,it->second.wintext);
	}
}

int  TextPair::GetInfo(DWORD zPos ,int &len, char *dst)
{
	len = -1;
	it = TextLayer.find(zPos);
	if(it != TextLayer.end())
	{
		memcpy(dst,it->second.wintext,it->second.len);
		len = it->second.len;
		return 1;//success
		fprintf(filehandle,"String Found![%08x]\n",zPos);
	}
	
	else
	{
		//printf("A fatal ocurred!\n");
		return 0;
	}
}

bool TextPair::Find(DWORD zPos)
{
	it = TextLayer.find(zPos);
	if(it == TextLayer.end()) return false;
	else return true;
}

void TextPair::Close()
{
	if(filehandle);
	fclose(filehandle);
}

TextPair::TextPair()
{
	//offset = 0;
	//memset(wintext,0,sizeof(wintext));
	filehandle = 0;
	filehandle = fopen("STR2.txt","wb");
}

TextPair::~TextPair()
{
	
}

void TextPair::Add(DWORD zPos, const char *rhs, int len)
{
	it = TextLayer.find(zPos);
	if(it != TextLayer.end())
	{
		fprintf(filehandle,"[Find an error!]%08x\r\n",zPos);
	}
	TextLayerPair tmp;
	tmp.first = zPos;
	memcpy(tmp.second.wintext , rhs, len);
	tmp.second.len = (unsigned char)len;
	TextLayer.insert(tmp);
}

int ShowState(DWORD in, DWORD all)
{
	if((in - 4 >= 0)&&(all - 4 > 0))
	{
		char buf[100];
		sprintf(buf, "Process: %.2f[@ %08x]\n", (float)(in - 4)/(float)(all - 4), in);
		SetConsoleTitleA(buf);
	}

	return 0;
}


